        const fetchapi = async()=>{
            setdailydata(await fetchdailydata());
           // console.log(await fetchdailydata());
        }
        fetchapi();
    },[]);
    const linechart =(
        dailydata.length !== 0?
        (<Line
            data ={{
                labels:dailydata.map(({date})=>date),
                datasets:[{
                    data: dailydata.map(({confirmed})=>confirmed),
                    label: 'Infected',
                    borderColor: '#3333ff',
                    fill : true
                },{
                    data: dailydata.map(({deaths})=>deaths),
                    label: 'Deaths',
                    borderColor: 'red',
                    fill : true
                }]
            }}
        />): null
    );
